// src/pages/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAuth, GoogleAuthProvider, signInWithPopup } from 'firebase/auth';
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";

// ‚úÖ Firebase config
const firebaseConfig = {
  apiKey: "AIzaSyBTkwcfkXg9FnzaJGKS5iuHiNvkHKLHywA",
  authDomain: "chatbot-authentication-ef411.firebaseapp.com",
  projectId: "chatbot-authentication-ef411",
  storageBucket: "chatbot-authentication-ef411.appspot.com", // fixed typo from "firebasestorage.app"
  messagingSenderId: "964739824417",
  appId: "1:964739824417:web:45519389e74972b9a1dc60",
  measurementId: "G-VWF0XY3JCR"
};

// ‚úÖ Initialize Firebase
const app = initializeApp(firebaseConfig);
getAnalytics(app);
const auth = getAuth(app);

const Login = () => {
  const [isSigningIn, setIsSigningIn] = useState(false);
  const navigate = useNavigate(); // üöÄ To redirect after login

  const handleGoogleLogin = async () => {
    if (isSigningIn) return;
    setIsSigningIn(true);

    const provider = new GoogleAuthProvider();

    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;

      console.log('‚úÖ Logged in as:', user.displayName);
      alert(`Welcome, ${user.displayName}!`);

      // ‚úÖ Optional: Save user to localStorage
      localStorage.setItem('user', JSON.stringify({
        name: user.displayName,
        email: user.email,
        photo: user.photoURL
      }));

      // üöÄ Redirect to interview page
      navigate('/interview');
    } catch (error) {
      console.error('‚ùå Google sign-in failed:', error.message);
      alert('Login failed: ' + error.message);
    } finally {
      setIsSigningIn(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <button
        onClick={handleGoogleLogin}
        disabled={isSigningIn}
        className={`px-6 py-3 rounded text-white ${
          isSigningIn ? 'bg-gray-500 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700'
        }`}
      >
        {isSigningIn ? 'üîÑ Signing in...' : 'üîê Sign in with Google'}
      </button>
    </div>
  );
};

export default Login;
