import axios from 'axios';

const BASE_URL = 'http://localhost:5000/api/interview';

const interviewAPI = {
  ask: async (question, answer) => {
    const res = await axios.post(`${BASE_URL}/ask`, { question, answer });
    return res.data;
  },
  evaluate: async (answer) => {
    const res = await axios.post(`${BASE_URL}/evaluate`, { answer });
    return res.data;
  },
  getNextQuestion: async () => {
    const res = await axios.get(`${BASE_URL}/next-question`);
    return res.data.question;
  }
};

export default interviewAPI;
