import React, { useState } from 'react';
import { ask, evaluate } from '../api/interviewAPI';

const ChatBox = () => {
  const [question, setQuestion] = useState('');
  const [userAnswer, setUserAnswer] = useState('');
  const [feedback, setFeedback] = useState('');
  const [score, setScore] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleNext = async () => {
    setLoading(true);
    try {
      const res = await ask(); // call backend to get question
      setQuestion(res.question);
      setUserAnswer('');
      setFeedback('');
      setScore(null);
    } catch (err) {
      console.error("Error asking question:", err);
      alert("Error: Could not get question.");
    }
    setLoading(false);
  };

  const handleSubmit = async () => {
    if (!userAnswer.trim()) return alert("Please enter your answer.");
    setLoading(true);
    try {
      const res = await evaluate(question, userAnswer);
      setScore(res.score);
      setFeedback(res.feedback);
    } catch (err) {
      console.error("Error evaluating answer:", err);
      alert("Error: Could not evaluate answer.");
    }
    setLoading(false);
  };

  return (
    <div className="p-6 max-w-xl mx-auto bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">AI Interview Chatbot</h2>

      {!question && (
        <button
          onClick={handleNext}
          className="bg-blue-600 text-white px-4 py-2 rounded"
        >
          Start Interview
        </button>
      )}

      {question && (
        <>
          <div className="mb-3">
            <strong>Question:</strong> {question}
          </div>

          <textarea
            className="w-full border p-2 rounded mb-3"
            rows="4"
            value={userAnswer}
            onChange={(e) => setUserAnswer(e.target.value)}
            placeholder="Type your answer here..."
          />

          <button
            onClick={handleSubmit}
            className="bg-green-600 text-white px-4 py-2 rounded mr-2"
          >
            Submit
          </button>

          <button
            onClick={handleNext}
            className="bg-gray-500 text-white px-4 py-2 rounded"
          >
            Next Question
          </button>
        </>
      )}

      {score !== null && (
        <div className="mt-4">
          <p className="text-lg font-semibold">Score: {score}/10</p>
          <p className="mt-2 whitespace-pre-line">{feedback}</p>
        </div>
      )}

      {loading && <p className="mt-3 text-blue-500">‚è≥ Processing...</p>}
    </div>
  );
};

export default ChatBox;
