// src/pages/TopicSelect.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

const topics = ['React', 'Data Structures', 'DBMS', 'System Design', 'JavaScript', 'C++'];

const TopicSelect = () => {
  const navigate = useNavigate();

  const handleSelect = (topic) => {
    // You can store the selected topic in localStorage or context
    localStorage.setItem('selectedTopic', topic);
    navigate('/interview');
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      <h1 className="text-2xl font-bold mb-4">ðŸŽ¯ What do you want to prepare for?</h1>
      <div className="grid grid-cols-2 gap-4">
        {topics.map((topic) => (
          <button
            key={topic}
            onClick={() => handleSelect(topic)}
            className="px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700"
          >
            {topic}
          </button>
        ))}
      </div>
    </div>
  );
};

export default TopicSelect;
