const handleNext = async () => {
  try {
    const askRes = await ask(); // call ask API
    const question = askRes.data.question;
    setCurrentQuestion(question);
    setMessages((prev) => [...prev, { from: 'bot', text: question }]);
  } catch (err) {
    console.error('Error asking question:', err);
    setMessages((prev) => [...prev, { from: 'bot', text: '❌ Failed to get question.' }]);
  }
};

const handleSubmit = async () => {
  if (!userInput.trim()) return;

  setMessages((prev) => [...prev, { from: 'user', text: userInput }]);

  try {
    const evalRes = await evaluate(userInput); // call evaluate API
    const userScore = evalRes.data.score;
    setScore(userScore);
    setMessages((prev) => [
      ...prev,
      { from: 'bot', text: `Score: ${userScore}/10` },
    ]);

    const fbRes = await feedback(userScore); // call feedback API
    const fb = fbRes.data.feedback;
    setFeedbackText(fb);
    setMessages((prev) => [...prev, { from: 'bot', text: fb }]);
  } catch (err) {
    console.error('Error evaluating answer:', err);
    setMessages((prev) => [...prev, { from: 'bot', text: '❌ Evaluation failed.' }]);
  }

  setUserInput('');
};
