import React, { useState } from 'react';
import { ask, evaluate } from '../api/interviewAPI';

const ChatBox = () => {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [response, setResponse] = useState('');
  const [listening, setListening] = useState(false);

  const handleNext = async () => {
    try {
      const res = await ask();
      setQuestion(res.question);
      setAnswer('');
      setResponse('');
    } catch (err) {
      console.error("Error asking question:", err);
    }
  };

  const handleEvaluate = async () => {
    try {
      const res = await evaluate({ question, answer });
      setResponse(res.feedback);
    } catch (err) {
      console.error("Error evaluating answer:", err);
    }
  };

  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser doesn't support speech recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;

    recognition.onstart = () => setListening(true);
    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setAnswer(transcript);
      setListening(false);
    };
    recognition.onerror = () => setListening(false);
    recognition.onend = () => setListening(false);

    recognition.start();
  };

  return (
    <div className="p-6 space-y-4 max-w-xl mx-auto bg-white shadow-xl rounded-xl">
      <button
        onClick={handleNext}
        className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
      >
        üéØ Get New Question
      </button>

      {question && (
        <>
          <h3 className="text-xl font-bold text-gray-800">{question}</h3>

          <textarea
            className="w-full border border-gray-300 p-3 rounded-lg text-gray-700"
            rows="4"
            placeholder="Type or speak your answer..."
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
          ></textarea>

          <div className="flex gap-4 items-center">
            <button
              onClick={startListening}
              className={`px-4 py-2 rounded-full text-white text-xl transition ${
                listening ? 'bg-red-600 animate-pulse' : 'bg-green-600 hover:bg-green-700'
              }`}
              title="Click to speak"
            >
              üé§
            </button>

            <button
              onClick={handleEvaluate}
              className="bg-purple-600 text-white px-6 py-2 rounded-lg hover:bg-purple-700"
            >
              ‚úÖ Submit Answer
            </button>
          </div>
        </>
      )}

      {response && (
        <div className="bg-gray-100 border-l-4 border-green-500 p-4 rounded">
          <h4 className="font-semibold">üìù Feedback:</h4>
          <p className="text-gray-700">{response}</p>
        </div>
      )}
    </div>
  );
};

export default ChatBox;
