import React, { useState } from 'react';
import { ask, evaluate } from '../api/interviewAPI';

const ChatBox = () => {
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [response, setResponse] = useState('');
  const [listening, setListening] = useState(false);

  const handleNext = async () => {
    try {
      const res = await ask();
      setQuestion(res.question);
    } catch (err) {
      console.error("Error asking question:", err);
    }
  };

  const handleEvaluate = async () => {
    try {
      const res = await evaluate({ question, answer });
      setResponse(res.feedback);
    } catch (err) {
      console.error("Error evaluating answer:", err);
    }
  };

  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      alert("Your browser doesn't support speech recognition.");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;

    recognition.onstart = () => {
      setListening(true);
    };

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setAnswer(transcript);
      setListening(false);
    };

    recognition.onerror = (event) => {
      console.error("Speech recognition error", event.error);
      setListening(false);
    };

    recognition.start();
  };

  return (
    <div className="p-4 space-y-4">
      <button onClick={handleNext} className="bg-blue-600 text-white px-4 py-2 rounded">Get Question</button>
      {question && (
        <>
          <h3 className="text-lg font-semibold">{question}</h3>
          <textarea
            className="w-full border p-2 rounded"
            rows="4"
            value={answer}
            onChange={(e) => setAnswer(e.target.value)}
            placeholder="Type or speak your answer..."
          ></textarea>
          <div className="flex items-center space-x-4">
            <button onClick={startListening} className="bg-green-600 text-white px-4 py-2 rounded">
              ðŸŽ¤ {listening ? 'Listening...' : 'Speak Answer'}
            </button>
            <button onClick={handleEvaluate} className="bg-purple-600 text-white px-4 py-2 rounded">
              Submit Answer
            </button>
          </div>
        </>
      )}
      {response && (
        <div className="bg-gray-100 p-3 rounded">
          <strong>Feedback:</strong>
          <p>{response}</p>
        </div>
      )}
    </div>
  );
};

export default ChatBox;
