import React, { useState, useEffect, useRef } from 'react';
import { ask, evaluate, feedback } from '../api/interviewAPI';

const ChatBox = () => {
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');
  const [currentQuestion, setCurrentQuestion] = useState('');
  const [score, setScore] = useState(null);
  const [feedbackText, setFeedbackText] = useState('');
  const [listening, setListening] = useState(false);
  const [isBotTyping, setIsBotTyping] = useState(false);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleNext = async () => {
    try {
      setIsBotTyping(true);
      const askRes = await ask();
      const question = askRes.data.question;
      setCurrentQuestion(question);
      setMessages((prev) => [...prev, { from: 'bot', text: question }]);
    } catch (err) {
      console.error('Error asking question:', err);
      setMessages((prev) => [...prev, { from: 'bot', text: 'âŒ Failed to fetch question.' }]);
    } finally {
      setIsBotTyping(false);
    }
  };

  const handleSubmit = async () => {
    if (!userInput.trim()) return;

    setMessages((prev) => [...prev, { from: 'user', text: userInput }]);

    try {
      setIsBotTyping(true);

      // Call evaluate (score + explanation)
      const evalRes = await evaluate(userInput);
      const userScore = evalRes.data.score;
      const explanation = evalRes.data.explanation;

      setScore(userScore);
      setMessages((prev) => [
        ...prev,
        { from: 'bot', text: `âœ… Score: ${userScore}/10` },
        { from: 'bot', text: explanation }
      ]);

      // Feedback (optional if not needed, you can skip this)
      const fbRes = await feedback(userInput);
      const fb = fbRes.data.feedback;
      setFeedbackText(fb);
      setMessages((prev) => [...prev, { from: 'bot', text: `ðŸ“ Feedback: ${fb}` }]);
    } catch (err) {
      console.error('Error evaluating answer:', err);
      setMessages((prev) => [...prev, { from: 'bot', text: 'âŒ Failed to evaluate answer.' }]);
    } finally {
      setUserInput('');
      setIsBotTyping(false);
    }
  };

  const startListening = () => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

    if (!SpeechRecognition) {
      alert('ðŸŽ¤ Speech recognition is not supported in this browser.');
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.interimResults = false;

    recognition.onstart = () => setListening(true);
    recognition.onend = () => setListening(false);

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setUserInput(transcript);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      setListening(false);
    };

    recognition.start();
  };

  const addEmoji = (emoji) => {
    setUserInput((prev) => prev + ' ' + emoji);
  };

  return (
    <div className="p-4 max-w-xl mx-auto border rounded-lg shadow-md bg-white">
      <div className="h-64 overflow-y-auto mb-4 border p-2 rounded bg-gray-50">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-2 p-2 rounded-lg max-w-[85%] ${
              msg.from === 'bot' ? 'bg-blue-100 text-blue-800' : 'bg-green-100 text-green-800 self-end'
            }`}
          >
            <strong>{msg.from === 'bot' ? 'ðŸ¤– Bot:' : 'ðŸ§‘ You:'}</strong> {msg.text}
          </div>
        ))}
        {isBotTyping && <div className="text-blue-500 italic mb-2">ðŸ¤– Bot is typing...</div>}
        <div ref={messagesEndRef} />
      </div>

      <div className="flex flex-wrap gap-1 mb-2">
        {['ðŸ‘', 'ðŸ˜Š', 'ðŸ˜•', 'ðŸ”¥', 'ðŸŽ¯'].map((emoji) => (
          <button
            key={emoji}
            onClick={() => addEmoji(emoji)}
            className="text-xl hover:scale-125 transition-transform"
          >
            {emoji}
          </button>
        ))}
      </div>

      <div className="flex gap-2 items-center">
        <input
          type="text"
          className="flex-1 p-2 border rounded"
          placeholder="Type or speak your answer..."
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSubmit()}
        />

        <button
          onClick={startListening}
          title="Click to speak"
          className={`px-3 py-2 rounded-full text-xl ${
            listening
              ? 'bg-red-600 text-white animate-pulse'
              : 'bg-green-600 text-white hover:bg-green-700'
          }`}
        >
          ðŸŽ¤
        </button>

        <button
          onClick={handleSubmit}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit
        </button>

        <button
          onClick={handleNext}
          className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default ChatBox;
