import React, { useState } from 'react';
import QuestionCard from './QuestionCard';
import interviewAPI from '../api/interviewAPI';

const ChatBox = ({ setSessionData, setScore, setFeedback }) => {
  const [question, setQuestion] = useState('Tell me about yourself.');
  const [userAnswer, setUserAnswer] = useState('');
  const [history, setHistory] = useState([]);

  const handleNext = async () => {
    const response = await interviewAPI.ask(question, userAnswer);
    const evaluation = await interviewAPI.evaluate(userAnswer);

    const newEntry = {
      question,
      answer: userAnswer,
      feedback: evaluation.feedback,
      score: evaluation.score,
    };

    setHistory([...history, newEntry]);
    setSessionData(prev => [...prev, newEntry]);
    setScore(evaluation.score);
    setFeedback(evaluation.feedback);

    const newQuestion = await interviewAPI.getNextQuestion();
    setQuestion(newQuestion);
    setUserAnswer('');
  };

  return (
    <div className="mb-6">
      <QuestionCard
        question={question}
        answer={userAnswer}
        setAnswer={setUserAnswer}
        onNext={handleNext}
      />
    </div>
  );
};

export default ChatBox;
